$(document).ready(function () { var a = $(window).scrollTop(), i = $("nav").offset().top, t = !0; $(window).scroll(function () { var o = $(this).scrollTop(); if (a < o) { if (t) { var n = i -= 150; $("nav").animate({ top: n }, 400), t = !1 } } else if (!t) { n = i += 150; $("nav").animate({ top: n }, 400), t = !0 } a = o }) });